import React, { useEffect, useState } from "react";
import Axios from "axios";
import image from "../images/addbook.jpg";

function Myblogs() {
  const [fdata, setFData] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:4000/book")
      .then((res) => {
        console.log(res.data);
        setFData(res.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  return (
    <div>
      
      <h1 className="text-center mt-4 mb-3" style={{ color: "darkblue", fontWeight: "bold", fontSize: "34px" }}>
  Available Books
</h1>

     

      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
        {fdata.map((book) => (
          <div className="col-md-4 col-lg-4 col-sm-12" key={book._id}>
            <div className="card">
              <img src={image} className="card-img-top" alt="..." />
              <div className="card-body">
                <h5 className="card-title">{book.title}</h5>
                <p className="card-text">Author: {book.author}</p>
                <p className="card-text">Genre: {book.genre}</p>
                <div className="d-flex justify-content-between">
                <a href="#" className="btn btn-primary">Update</a>
    <div style={{ width: "10px" }}></div> {/* Add some spacing */}
    <a href="#" className="btn btn-primary">Delete</a>
  </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Myblogs;
