import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import photo1 from "../images/carousal-1.jpg"
import photo2 from "../images/carousal-2.jpg"
import photo3 from "../images/carousal-3.jpg"
const Home = () => {

  const [fdata, setFData] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState(fdata);

  useEffect(() => {
    const filtered = fdata.filter(
      (book) =>
        book.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        book.author.toLowerCase().includes(searchQuery.toLowerCase()) ||
        book.genre.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredBooks(filtered);
  }, [fdata, searchQuery]);
  


  useEffect(() => {
    Axios.get("http://localhost:4000/book").then((res) => {
      setFData(res.data);
    });
  }, []);

  const navigate = useNavigate();
  const [cookies, removeCookie] = useCookies([]);
  const [username, setUsername] = useState("");
  
  useEffect(() => {


    const verifyCookie = async () => {
      
      if (!cookies.token) {
        navigate("/login");
      }
      const { data } = await axios.post(
        "http://localhost:4000",
        {},
        { withCredentials: true }
      );
      
      const { status, user } = data;
      setUsername(user);
      return status
        ? toast(`Hello ${user}`, {
            position: "top-right",
          })
        : (removeCookie("token"), navigate("/login"));
    };
    verifyCookie();
  }, [cookies, navigate, removeCookie]);
  
  
  const Logout = () => {
    removeCookie("token");
    navigate("/login");
  };

  return ( 
      <> 
      <body>
        
        {/* navbar */}
        <nav class="navbar navbar-expand-lg bg-warning">
  <div class="container-fluid">
    <h2 class="navbar-brand" >Library</h2>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="/">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link active" href="/Service">Service</a>
        </li>
      
        <li class="nav-item">
          <a class="nav-link active" href="/about">About</a>
        </li>
        </ul>
      <form class="d-flex" >
        <button class="btn btn-outline-success" onClick={Logout}>Logout</button>
      </form>
    </div>
    <br></br>
  </div>
</nav>

<br></br>










<div id="carouselExample" class="carousel slide">
        <div class="carousel-inner">
          <div class="carousel-item active">
            <img
              src={photo1}
              class="d-block w-100"
              alt="..."
              style={{width:"200px",height:"550px",objectFit:"cover"}}
            ></img>
          </div>
          <div class="carousel-item">
            <img
              src={photo2}
              class="d-block w-100"
              alt="..."
              style={{width:"200px",height:"550px",objectFit:"cover"}}
            ></img>
          </div>
          <div class="carousel-item">
            <img
              src={photo3}
              class="d-block w-100"
              alt="..."
              style={{width:"200px",height:"550px",objectFit:"cover"}}
            ></img>
          </div>
        </div>
        <button
          class="carousel-control-prev"
          type="button"
          data-bs-target="#carouselExample"
          data-bs-slide="prev"
        >
          <span class="carousel-control-prev-icon" aria-hidden="true"></span>
          <span class="visually-hidden">Previous</span>
        </button>
        <button
          class="carousel-control-next"
          type="button"
          data-bs-target="#carouselExample"
          data-bs-slide="next"
        >
          <span class="carousel-control-next-icon" aria-hidden="true"></span>
          <span class="visually-hidden">Next</span>
        </button>
      </div>
    <br></br>
<center><h1 style={{position:"center",fontFamily:"sans-serif"}}>New Arrivals!!</h1></center>
