import React, { useState, useEffect } from "react";
import Axios from "axios";
import { useParams } from "react-router-dom";

function UpdateBook() {
  const { id } = useParams();
  const [book, setBook] = useState({
    title: "",
    author: "",
    genre: "",
  });

  useEffect(() => {
    Axios.get(`http://localhost:4000/book/${id}`).then((res) => {
      setBook(res.data);
    });
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBook((prevBook) => ({
      ...prevBook,
      [name]: value,
    }));
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    Axios.put(`http://localhost:4000/book/${id}`, book)
      .then((res) => {
        console.log("Book updated:", res.data);
        // Redirect to the book list page or handle navigation as needed
      })
      .catch((error) => {
        console.error("Error updating book:", error);
      });
  };

  return (
<>

    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="card shadow" style={{ width: "18rem" }}>
        <img src="path_to_your_image.jpg" className="card-img-top" alt="Card" />
        <div className="card-body">
          <h5 className="card-title">Card Title</h5>
          <p className="card-text">
            Some example text. You can add more text here to describe the card's content.
          </p>
          <button className="btn btn-primary">Click Me</button>
        </div>
      </div>
    </div>



















    <div>
      <h2>Update Book</h2>
      <form onSubmit={handleUpdate}>
        <div>
          <label>Title:</label>
          <input
            type="text"
            name="title"
            value={book.title}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Author:</label>
          <input
            type="text"
            name="author"
            value={book.author}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Genre:</label>
          <input
            type="text"
            name="genre"
            value={book.genre}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit">Update</button>
      </form>
    </div>
    </>
  );
}

export default UpdateBook;
