import React, { useState, useEffect } from "react";
import Axios from "axios";

import { useParams } from "react-router-dom";

import { Link, useNavigate } from "react-router-dom";
import "./UpdateForm.css"

function UpdateBook() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState({
    title: "",
    author: "",
    genre: "",
    year:"",
    image:"",
  });

  useEffect(() => {
    Axios.get(`http://localhost:4000/book/${id}`).then((res) => {
      setBook(res.data);
    });
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBook((prevBook) => ({
      ...prevBook,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setBook({
          ...book,
          image: reader.result, // Store the base64 encoded image
        });
      };
      reader.readAsDataURL(file);
    }
  };


  const handleUpdate = (e) => {
    e.preventDefault();
    Axios.put(`http://localhost:4000/book/${id}`, book)
      .then((res) => {
        console.log("Book updated:", res.data);
        alert("Updated")
        setTimeout(() => {
          navigate("/view");
        }, 1000);
        // Redirect to the book list page or handle navigation as needed
      })
      .catch((error) => {
        console.error("Error updating book:", error);
       alert(error)
      });
  };

  return (
