import React, { useState, useEffect } from "react";
import axios from "axios";

const UpdateBook = ({ match }) => {
  const bookId = match.params.id;
  const [book, setBook] = useState({});
  const [updatedBook, setUpdatedBook] = useState({
    title: "",
    author: "",
    genre: "",
  });

  useEffect(() => {
    // Fetch the book data based on bookId
    axios.get(`http://localhost:4000/book/${bookId}`).then((response) => {
      setBook(response.data);
      setUpdatedBook(response.data);
    });
  }, [bookId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedBook((prevBook) => ({
      ...prevBook,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:4000/b0k/${bookId}`, updatedBook);
      // Redirect to the book list page or handle navigation as needed
    } catch (error) {
      console.error("Error updating book:", error);
    }
  };

  return (
    <div>
      <h2>Update Book</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input
            type="text"
            name="title"
            value={updatedBook.title}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Author:</label>
          <input
            type="text"
            name="author"
            value={updatedBook.author}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Genre:</label>
          <input
            type="text"
            name="genre"
            value={updatedBook.genre}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default UpdateBook;
