import "./service.css"
import axios from "axios";
import updateBook from "../images/update_book.png";
import addBook from "../images/add_book.png"

import React, {useState } from 'react';
import "./addBook.css"




const Service = () => {



  const [inputValue, setInputValue] = useState({
    title: "",
    author: "",
    genre: "",
    year:"",
    image: "",
  });

  const { title  , author , genre, year } = inputValue;

  const handleOnChange = (e) => {
    const {id, value } = e.target;
    setInputValue({
      ...inputValue,
      [id]: value,
    });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setInputValue({
          ...inputValue,
          image: reader.result, // Store the base64 encoded image
        });
      };
      reader.readAsDataURL(file);
    }
  };


 

  
    const handleSubmit = async (e) => {
      e.preventDefault();
      
      try {
        const { data } = await axios.post(
          "http://localhost:4000/addbook",
          {
            ...inputValue,
          },
          { withCredentials: true   }
        );
        const { success, message } = data;
        if (success) {
          alert("Book Added Successfully !!!")
         
        } else {
          
          alert(message)
        }
      } catch (error) {
        console.log(error);
      }
      setInputValue({
        ...inputValue,
        title: "",
        author: "",
        genre: "",
        year:"",
        image: "",
        
      });
    };
  
    return ( 
        <>
        <body>
            
    
       <br></br>
    <div class="container-fluid">
        <div class="row">
        <div class=" col-sm-12 col-md-4 col-lg-2">
              
            </div>
            <div class=" col-sm-12 col-md-4 col-lg-3">
                <div class="card">
                    <img src={addBook} width="auto" height="250px" alt="not found"></img>                
                    <div class="card-body">
                        <h5 class="card-title">Add Book</h5>
                        <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal"> Add</button>
                    </div>
                </div>
            </div>
            <div class=" col-sm-12 col-md-4 col-lg-2">
                {/* <div class="card">
                    <img src={borrowBook} width="auto" height="250px"></img>
                    <div class="card-body">
                        <h5 class="card-title">Borrow Book</h5>
                        <a href="#" class="btn btn-primary">Borrow</a>
                    </div>
                </div> */}
            </div>
            <div class=" col-sm-12 col-md-4 col-lg-3">
                <div class="card">
                    <img src={updateBook} width="auto" height="250px" alt="not found"></img>
                    <div class="card-body">
                        <h5 class="card-title">View Book</h5>
                        <a href="/view" class="btn btn-primary">View</a>
                    </div>
                </div>
            </div>
            <div class=" col-sm-12 col-md-4 col-lg-2">
               
            </div>
        </div>
    </div>
      
    

{/* adding book */}

<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">Add a Book to the Library</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <div>


        <form  onSubmit={handleSubmit}>
                  <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">
                      Book Name
                    </label>
                    <input
                       type="text"
                       className="form-control"
                       id="title"
                       placeholder="Enter book title"
                       value={title}
                       onChange={handleOnChange}
                       required
                      aria-describedby="emailHelp"
                    ></input>
                  </div>
                  <div class="mb-3">
                    <label for="exampleInputPassword1" class="form-label">
                      Author Name
                    </label>
                    <input
                        type="text"
                        className="form-control"
                        id="author"
                        placeholder="Enter author name"
                        value={author}
                        onChange={handleOnChange}
                        required
                    ></input>
                  </div>
                  <div class="mb-3">
                    <label for="exampleInputPassword1" class="form-label">
                      Gener
                    </label>
                    <input
                       type="text"
                       className="form-control"
                       id="genre"
                       placeholder="Enter book genre"
                       value={genre}
                       onChange={handleOnChange}
                       required
                    ></input>
                  </div>
                  <div class="mb-3">
                    <label for="exampleInputPassword1" class="form-label">
                      Year of Publication
                    </label>
                    <input
                     type="number"
                     className="form-control"
                     id="year"
                     placeholder="Enter the year"
                     value={year}
                     onChange={handleOnChange}
                     required
                    ></input>
                  </div>
                  <div class="mb-3">
                    <label for="exampleInputPassword1" class="form-label">
                      Facing sheet image
                    </label>
                    <input
                      type="file"
                      class="form-control"
                      id="image"
                      placeholder='.jpeg file'
                      onChange={handleImageChange}
                      
                      accept="image/*"
                    ></input>
                  </div>

                
                <div class="modal-footer">
                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                <button type="submit" class="btn btn-primary">Add Book</button>
                </div>

                </form>
    
   </div> 
      </div>
      
    </div>
  </div>
</div>
   


<br></br>
<br></br>
<br></br>



<div>
<footer class="bg-light pb-5">
      <div class="container text-center">
        <div>
          <div>Contact US</div>
          <div>Phone:+91 7025047555</div>
          <div>E-Mail:edu@gmail.com</div>
        </div>
        <p class="font-italic text-muted mb-0">
          &copy; Copyrights education.com All rights reserved.
        </p>
      </div>
    </footer>
 </div>
      
</body>
        </>
     );
}

export default Service;
