import "./service.css"
import axios from "axios";
import updateBook from "../images/update_book.png";
import addBook from "../images/add_book.png"
import { ToastContainer, toast } from "react-toastify";
import React, {useState } from 'react';
const Service = () => {

  // const [fbooks, setBooks] = useState([]);

  // useEffect(() => {
  //   // Fetch data from the server
  //   axios.get('http://localhost:4000/books')
  //     .then(response => {
  //       setBooks(response.data);
  //     })
  //     .catch(error => {
  //       console.error('Error fetching data:', error);
  //     });
  // }, []);

    

  const [inputValue, setInputValue] = useState({
    title: "",
    author: "",
    genre: "",
    year:"",
    image: null,
  });

  const { title  , author , genre, year ,image} = inputValue;

  const handleOnChange = (e) => {
    const {id, value } = e.target;
    setInputValue({
      ...inputValue,
      [id]: value,
    });
  };

  const handleImageChange = (e) => {
    const imageFile = e.target.files[0]; // Get the selected image file
    setInputValue({
      ...inputValue,
      image: imageFile,
    });
  };


 

  
    const handleSubmit = async (e) => {
      e.preventDefault();
      const formData = new FormData(); // Create a FormData object
      formData.append("title", title);
      formData.append("author", author);
      formData.append("genre", genre);
      formData.append("year", year);
      formData.append("image", image);
      try {
        const { data } = await axios.post(
          "http://localhost:4000/addbook",
          formData,
          { withCredentials: true ,
            headers: {
              "Content-Type": "multipart/form-data", // Set the content type
            },
          }
        );
        const { success, message } = data;
        if (success) {
          alert("Book Added Successfully !!!")
         
        } else {
          
          alert(message)
        }
      } catch (error) {
        console.log(error);
      }
      setInputValue({
        ...inputValue,
        title: "",
        author: "",
        genre: "",
        year:"",
        image: null,
        
      });
    };
  
    return ( 
        <>
        <body>
            
    
       <br></br>
    <div class="container-fluid">
        <div class="row">
        <div class=" col-sm-12 col-md-4 col-lg-2">
                {/* <div class="card">
                    <img src={addBook} width="auto" height="250px"></img>                
                    <div class="card-body">
                        <h5 class="card-title">Add Book</h5>
                        <a href="#" class="btn btn-primary">Add</a>
                    </div>
                </div> */}
            </div>
            <div class=" col-sm-12 col-md-4 col-lg-3">
                <div class="card">
                    <img src={addBook} width="auto" height="250px"></img>                
                    <div class="card-body">
                        <h5 class="card-title">Add Book</h5>
                        <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal"> Add</button>
                    </div>
                </div>
            </div>
            <div class=" col-sm-12 col-md-4 col-lg-2">
                {/* <div class="card">
                    <img src={borrowBook} width="auto" height="250px"></img>
                    <div class="card-body">
                        <h5 class="card-title">Borrow Book</h5>
                        <a href="#" class="btn btn-primary">Borrow</a>
                    </div>
                </div> */}
            </div>
            <div class=" col-sm-12 col-md-4 col-lg-3">
                <div class="card">
                    <img src={updateBook} width="auto" height="250px"></img>
                    <div class="card-body">
                        <h5 class="card-title">View Book</h5>
                        <a href="/books" class="btn btn-primary">View</a>
                    </div>
                </div>
            </div>
            <div class=" col-sm-12 col-md-4 col-lg-2">
                {/* <div class="card">
                    <img src={deleteBook} width="auto" height="250px"></img>
                    <div class="card-body">
                        <h5 class="card-title">Delete Book</h5>
                        <a href="#" class="btn btn-primary">Delete</a>
                    </div>
                </div> */}
            </div>
        </div>
    </div>
      
    

{/* adding book */}

<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">Add a Book to the Library</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <div>


        <form>
                  <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">
                      Book Name
